programming_language.polymorphism:
*General-Programming-Knowledge*

0   

1   //Example of polymorphism

2   

3   #include <iostream>  

4   

5   /*

6   

7   The word polymorphism means having many forms. 

8   

9   Typically, polymorphism occurs when there is a hierarchy of classes 

10  and they are related by inheritance.

11  

12  C++ polymorphism means that a call to a member function will cause a 

13  different function to be executed depending on the type of object 

14  that invokes the function. 

15  

16  Example: if a parent class function is set to be overridable, 

17  then a parent class pointer can have functionality of the 

18  child class object.

19  

20  A pure virtual function is a function that is declared in 

21  the parent class and it should be defined in every child classes.

22  

23  Syntax: virtual void DisplayFunction () = 0;

24  

25  */

26  

27  using namespace std;

28  

29  class Shape

30  {

31      //By default a member without access-specifier is set to private.

32      protected:

33          int length;

34          int height;

35  

36      public:

37          //Each child class has to provide its own definition of this method.

38          virtual void DisplayInfo() 

39          {

40              cout << "This is the parent class." << endl;

41          }

42  };

43  

44  class Square: public Shape

45  {

46      private:

47          int area;

48          void CalcArea()

49          {

50              area=length*height;

51          }

52  

53      public:

54          Square (int len, int hg)

55          {

56              length=len;

57              height=hg;

58              CalcArea();

59          };

60          

61          void DisplayArea()

62          {

63              cout << "Area: " << area << endl;

64          }

65          

66          //This function overrides the parent class function

67          void DisplayInfo()

68          {

69              cout << "This is the child class." << endl;

70          }

71  };

72  

73  class Circle: public Shape

74  {

75      private:

76          int radius;

77      

78      public:

79          Circle(int len)

80          {

81              radius=len/2;

82          }

83          void DisplayRadius()

84          {

85              cout << "Radius: " << radius << endl;

86          }

87  };

88  

89  int main()

90  {

91      Shape *p;

92      Circle circle_object(22);

93      Square square_object(4,3);

94      

95      /*

96      The pointer of type Shape can reference any of the 

97      Shape class children, but having only 

98      the functionality of Shape Class.

99      */

100     

101     p=&circle_object;

102     p->DisplayInfo();

103     p=&square_object;

104     p->DisplayInfo();

105     

106     int a;

107     cin >> a;

108     return 0;

109     

110     /*

111     Using same variable we accessed different behaviour for 

112     each child class.

113     */

114 }

115

programming_language.inheritance:
*General-Programming-Knowledge*


            In C++, it is possible to inherit attributes and methods from one class 
            to another. 



            We group the "inheritance concept" into two categories:





            - derived class (child) - the class that inherits from another class;

            - base class (parent) - the class being inherited from;



            To inherit from a class, use the : symbol.


0   

1   #include <iostream>  

2   #include <conio.h>  

3   

4   using namespace std;

5   

6   //BASE CLASS

7   class Shape{

8   

9       protected: 

10      //protected variables are visible into this class and the other classes 

11      //that are inherited from this.

12  

13      string name;

14          

15      public:

16      Shape(string newname)

17      {

18          name=newname;

19      }

20      string getName() const

21      {

22          return name;

23      }

24  };

25  

26  //CHILD CLASS ONE

27  class Circle : public Shape 

28  //making Circle class derive from the Shape class (setting the inheritance).

29  {

30      //inheritance is a way to make 2 classes share the same variables between each other

31  

32      double radius; //this variable can be private, or protected or public (anything).

33  

34      public:

35          Circle(string inheritedName, double newradius) : Shape (inheritedName)

36          //this will call the Shape constructor which will set a value into it's string 

37          //variable called 'name', in this way, a variable will be used in 2 classes.

38          {

39              radius=newradius;

40          }

41          double getCircleRadius() const

42          {

43              return radius;

44          }

45  

46  };

47  

48  //CHILD CLASS TWO

49  class Rectangle : public Shape 

50  // making the Rectangle class derive from Shape Class, 

51  //let the Rectangle Class, use the protected variables of the Shape Class.

52  {

53      double length, width;

54  

55      public:

56          Rectangle (string inheritedName2, double newlength, double newwidth): Shape (inheritedName2)

57          {

58              length=newlength;

59              width=newwidth;

60          }

61          double getRectangleLength() const

62          {

63              return length;

64          }

65          double getRectangleWidth () const

66          {

67              return width;

68          }

69  };

70  

71  int main()

72  {

73      Shape object("Shape Class String"); //create object

74      cout << object.getName() << endl; //calling a function from the class

75  

76      cout << endl << "CHILD ONE CLASS: " << endl;

77      

78      Circle objectchild ("Circle (stored in Shape Class String)", 3.0); //create object

79      

80      //calling a function from Shape Class using the object from Circle Class.

81      cout << objectchild.getName() << endl; 

82      cout << objectchild.getCircleRadius() << endl;

83  

84      cout << endl << "CHILD TWO CLASS: " << endl;

85      

86      Rectangle objectchild2("Rectangle (stored in Shape Class String)", 20.0, 65.0); //create object

87      

88      //calling a function from Shape Class using the object from Rectangle Class.

89      cout << objectchild2.getName() << endl; 

90      cout << objectchild2.getRectangleLength() << endl;

91      cout << objectchild2.getRectangleWidth() << endl;

92  

93      getch();

94      return 0;

95  }

96

programming_language.abstraction:
*General-Programming-Knowledge*

0   

1   // Example of Data Abstraction in C++

2   

3   #include <iostream>  

4   using namespace std;

5    

6   class implementAbstraction 

7   {

8       private:

9           int a, b;

10       

11      public:

12          // method to set values of private members

13          void set(int x, int y)

14          {

15              a = x;

16              b = y;

17          }

18       

19          void display()

20          {

21              cout << "a = " << a << endl;

22              cout << "b = " << b << endl;

23          }

24  };

25   

26  int main()

27  {

28      implementAbstraction obj;

29      obj.set(10, 20);

30      obj.display();

31      return 0;

32      

33      /*

34          OUTPUT:

35          a = 10

36          b = 20

37      */

38  }

