programming_language.operator-overloading:
*General-Programming-Knowledge*

0   

1   // Example of Operator Overloading

2   

3   #include <iostream> 

4   using namespace std;

5    

6   class Complex 

7   {

8       private:

9           int real, imag;

10       

11      public:

12          Complex(int r = 0, int i = 0)

13          {

14              real = r;

15              imag = i;

16          }

17       

18          // This is automatically called when '+' is used with

19          // between two Complex objects

20          Complex operator+(Complex const    & obj)

21          {

22              Complex res;

23              res.real = real + obj.real;

24              res.imag = imag + obj.imag;

25              return res;

26          }

27          void print() { cout << real << " + i" << imag << '\n'; }

28  };

29   

30  int main()

31  {

32      Complex c1(10, 5), c2(2, 4);

33      Complex c3 = c1 + c2;

34      c3.print();

35      

36      /*

37          OUTPUT:

38          12 + i9

39      */

40  }

