programming_language.exception-handling:
*General-Programming-Knowledge*



0   

1   // C++ example on how to use of try,catch and throw

2   // for exception handling.

3    

4   #include <iostream> 

5   #include <stdexcept> 

6   using namespace std;

7    

8   int main()

9   {

10   

11      // try block

12      try 

13      {

14          int numerator = 10;

15          int denominator = 0;

16          int res;

17   

18          // check if denominator is 0 then throw runtime

19          // error.

20          if (denominator == 0) 

21          {

22              throw runtime_error(

23                  "Division by zero not allowed!");

24          }

25   

26          // calculate result if no exception occurs

27          res = numerator / denominator;

28          //[printing result after division

29          cout << "Result after division: " << res << endl;

30      }

31      // catch block to catch the thrown exception

32      catch (const exception& e) 

33      {

34          // print the exception

35          cout << "Exception " << e.what() << endl;

36      }

37   

38      return 0;

39  }

40

