programming_language.abstract-classes:
*General-Programming-Knowledge*

0   

1   /*Example of abstract class*/

2   

3   #include <iostream> 

4    

5   using namespace std;

6    

7   // Base class

8   class Shape {

9      public:

10        // pure virtual function providing interface framework.

11        virtual int getArea() = 0;

12        void setWidth(int w) {

13           width = w;

14        }

15     

16        void setHeight(int h) {

17           height = h;

18        }

19     

20     protected:

21        int width;

22        int height;

23  };

24   

25  // Derived classes

26  class Rectangle: public Shape {

27     public:

28        int getArea() { 

29           return (width * height); 

30        }

31  };

32  

33  class Triangle: public Shape {

34     public:

35        int getArea() { 

36           return (width * height)/2; 

37        }

38  };

39   

40  int main(void) {

41     Rectangle Rect;

42     Triangle  Tri;

43   

44     Rect.setWidth(5);

45     Rect.setHeight(7);

46     

47     // Print the area of the object.

48     cout << "Total Rectangle area: " << Rect.getArea() << endl;

49  

50     Tri.setWidth(5);

51     Tri.setHeight(7);

52     

53     // Print the area of the object.

54     cout << "Total Triangle area: " << Tri.getArea() << endl; 

55  

56     return 0;

57  }

