programming_language.modular-programming:
*General-Programming-Knowledge*


            Example of modular programming in C++:

0   

1   //FILE: vectors_of_objects_mainfile.cpp

2   //In this file we put everything together

3   

4   #include "vectors_of_objects_class.h" 

5   //by this inclusion we get access to: vectors_of_objects_class

6   

7   //Function prototyping or declaration

8   void createObjects(vector <vectors_of_objects_class>&);

9   void printObjectsInfo(const vector <vectors_of_objects_class>&); 

10  

11  //The main function is the entry point of the program

12  int main()

13  {

14      //creating a vector of objects;

15      vector <vectors_of_objects_class> ObjectVector; 

16      

17      //Calling the function that populates the vector of objects.

18      createObjects(ObjectVector);

19      

20      //Calling the function that prints out the vector of objects.

21      printObjectsInfo(ObjectVector);

22      return 0;

23  }

24  

25  //Function definition

26  void createObjects(vector <vectors_of_objects_class>& newObjVec)

27  {

28      int age;

29      string name;

30      cout << "How many people do you wish to add? ";

31      int people;

32      cin >> people;

33      for(int i=0;i<people;i++)

34      {

35          cout << "People_" << i << " Name: ";

36          cin >> name; //get the name from the keyboard

37          cout << "People_" << i << " Age: ";

38          cin >> age; //get the age from the keyboard

39          

40           //creating the object with self made constructor

41          vectors_of_objects_class ObjectInVector(name, age);

42          

43           //calling the class functions

44          ObjectInVector.setAge(age);

45          ObjectInVector.setName(name);

46          newObjVec.push_back(ObjectInVector);

47      }

48      cout << endl;

49  }

50  

51  //defining the function

52  void printObjectsInfo(const vector <vectors_of_objects_class>& newObjVec) 

53  {

54      unsigned int sizeOfVector=newObjVec.size();

55      for(int i=0;i<sizeOfVector; i++)

56      {

57          cout << "People_" << i << " Name: " << newObjVec[i].getName() << endl;

58          cout << "People_" << i << " Age: " << newObjVec[i].getAge() << endl;

59      }

60  }

61  

62  


0   

1   //FILE: vectors_of_objects_class.cpp

2   //In this file we implement the vectors_of_objects_class interface

3   

4   #include "vectors_of_objects_class.h"

5   

6   //Implementing the custom vector class interface

7   

8   //Adding a default constructor

9   vectors_of_objects_class::vectors_of_objects_class() //default constructor

10  {

11      //constructor

12  }

13  

14  //Adding an overloading constructor that takes 2 arguments

