programming_language.decorators:
*General-Programming-Knowledge*

0   

1   /*Example of decorators*/

2   

3   #include <iostream> 

4   using namespace std;

5   

6   class Computer

7   {

8       public:

9           virtual void display()

10          {

11              cout << "I am a computer..." << endl;

12          }

13  };

14  

15  class CDDrive : public Computer

16  {

17      private:

18          Computer* c;

19          

20      public:

21          CDDrive(Computer* _c)

22          {

23              c = _c;

24          }

25          void display()

26          {

27              c->display();

28              cout << "with a CD Drive..." << endl;

29          }

30  };

31  

32  class Printer : public Computer

33  {

34      private:

35          CDDrive* d;

36          

37      public:

38          Printer(CDDrive* _d)

39          {

40              d = _d;

41          }

42          void display()

43          {

44              d->display();

45              cout << "with a printer..." << endl;

46          }

47  };

48  

49  int main()

50  {

51      Computer* c = new Computer();

52      CDDrive* d = new CDDrive(c);

53      Printer* p = new Printer(d);

54  

55      p->display();

56  }

