programming_language.encapsulation:
*General-Programming-Knowledge*

0   

1   using System;

2   

3   /* Members of the class should never be set to public, because public means that anyone 

4    * who works at that program is able to change the variable's value, so to avoid that, 

5    * all the members (sometimes called proprieties), should be private, 

6    * (or protected for inheritance purposes).

7    * 

8    * Encapsulation: 

9    * uses getter functions for getting the value of private members of the class;

10   * uses setter function for setting the value of a private members of the class;

11   */

12   

13  namespace project27_classes_encaptulation_and_exceptions_throwing

14  {

15      class Program

16      {

17          public static void Main(string[] args)

18          {

19              Encapsulation_Example pobject = new Encapsulation_Example();

20              pobject.SetAge(15);

21              pobject.SetName("Ellias Mustellar");

22              Console.WriteLine("Name: {0}\nAge: {1}", pobject.GetName(), pobject.GetAge());

23              Console.Write("Press any key to continue . . . ");

24              Console.ReadKey(true);

25          }

26      }

27      class Encapsulation_Example

28      {

29          //Members: (sometimes called properties)

30          

31          //as good programming habbit the private variables in a function 

32          //should start with underscore.

33          private int _age; 

34  

35          private string _Name;

36          

37          //Setters:

38          public void SetAge(int NewAge)

39          {

40              if(NewAge<=0)

41              {

42                  //An exception is an error message, that occures and terminates the 

43                  //program in case that something goes wrong.

44  

45                  //Syntax for throwing exception:

46                  throw new Exception("A person cannot have NULL or NEGATIVE age.");

47              }

48              _age=NewAge;

49          }

50          

51          public void SetName(string NewName)

52          {

53              //string.IsNullOrEmpty(string_name); // is a function 

54              //that checks if a string is null or empty

55              if(string.IsNullOrEmpty(NewName))

56              {

57                  throw new Exception("The name cannot be NULL or EMPTY.");

58              }

59              _Name = NewName;

60          }

61          

62          //Getters:

63          public int GetAge()

64          {

65              return this._age;

66          }

67          public string GetName()

68          {

69              return this._Name;

70          }

71      }

72  }

