programming_language.polymorphism:
*General-Programming-Knowledge*

0   

1   using System;

2   

3   namespace project25_polymorphism

4   {

5       class Program

6       {

7           public static void Main(string[] args)

8           {

9               

10              Parent [] objarray = new Parent[4];

11              objarray[0]=new Parent();

12              objarray[1]=new Child1();

13              objarray[2]=new Child2();

14              objarray[3]=new Child3();

15              

16              foreach(Parent k in objarray)

17              {

18                  k.DisplayInfo(); //each child has the properties of the parent class

19                  

20                  /* To see the information of each child class we could make a function which  

21                   * will hide the parent class function, and contain the updated information of

22                   * the child class. But a better way doing that is marking the parent class 

23                   * you'd wish to hide with virtual keyword, which will allow every child class

24                   * overide the method by their own needs.

25                   */

26              }

27              Console.Write("Press any key to continue . . . ");

28              Console.ReadKey(true);

29          }

30      }

31      class Parent

32      {

33          protected string FirstName, LastName;

34         

35          public Parent()

36          {

37              FirstName="FN";

38              LastName="LN";

39          }

40          

41          //The virtual keyword will make this function overidable for each child class.

42          public virtual void DisplayInfo()  

43          {

44              Console.WriteLine("This is parent class");

45          }

46      }

47      

48      class Child1:Parent

49      {

50          //This is the syntax: for overriding (overwriting) a function.

51          public override void DisplayInfo()

52          {

53              Console.WriteLine("This is a child 1 class.");

54          }

55      }

56      

57      class Child2:Parent

58      {

59          //This is the syntax: for overriding (overwriting) a function.

60          public override void DisplayInfo()

61          {

62              Console.WriteLine("This is a child 2 class.");

63          }

64      }

65      

66      class Child3:Parent

67      {

68          //This is the syntax: for overriding (overwriting) a function.

69          public override void DisplayInfo()

70          {

71              Console.WriteLine("This is a child 3 class.");

72          }

73      }

74  }

