programming_language.serialization:
*General-Programming-Knowledge*

0   

1   //Example of using serialization in C#

2   

3   using System;  

4   using System.IO;  

5   using System.Runtime.Serialization.Formatters.Binary;

6     

7   [Serializable]  //this will ensure that the class is serializable

8   class Student  

9   {  

10      int rollno;  

11      string name;  

12      public Student(int rollno, string name)  

13      {  

14          this.rollno = rollno;  

15          this.name = name;  

16      }  

17  }

18  

19  public class SerializeExample  

20  {  

21      public static void Main(string[] args)  

22      {  

23          FileStream stream = new FileStream("e:\\sss.txt", FileMode.OpenOrCreate);  

24          BinaryFormatter formatter=new BinaryFormatter();  

25            

26          Student s = new Student(101, "sonoo");  

27          formatter.Serialize(stream, s);  

28    

29          stream.Close();  

30      }  

31  }  

