programming_language.enums:
*General-Programming-Knowledge*
             The enum is like an array of boolean values, 
             in which only one can be true at a time.



             This is useful while creating game engines, or software engines, 
             to know when a certain option is available. 



             Also this is used to control and interract the states in a certain application.



0   

1   //Example of using enums in C#

2   

3   using System;

4   using System.Threading.Tasks;

5   using System.Text;

6   using System.Linq;

7   using System.Collections.Generic;

8   

9   namespace Software

10  {

11      class Software

12      {

13           /*

14            * NOTE: 

15            * Enums cannot be declared inside a function, or method. 

16            * NOT even within the Main function.

17            */

18          

19          enum Gamestate {Playing, Menu, Pause=6}; //from now on, Gamestate is like a datatype.

20          

21          /*

22           * From the moment of declaration the enum is initializing to true the first state. 

23           * In this case:

24           * Playing = true, until the programmer sets it otherwise.

25           * The elements of the enum can also hold values, 

26           * as it sets above, Pause = 6, Playing = 0, Menu = 1 

27           * (by default the    value set for each element is its index).

28           */

29          

30          public static int Main()

31          {

32              //Creating an object/instance for the enum;

33              Gamestate gamestate;

34              Console.WriteLine("Gamestate: " + Convert.ToInt32(Gamestate.Pause));

35              Console.WriteLine("Gamestate: " + Convert.ToInt32(Gamestate.Playing));

36              

37              //Changing the state of the Gamestate enum;

38              gamestate=Gamestate.Menu;

39              

40              /* Equivalence for this case:

41               * bool Playing = false;

42               * bool Menu = true;

43               * bool Pause = false;

44               */

45               

46              Console.WriteLine("Gamestate: " + gamestate);        

47              gamestate=Gamestate.Playing;

48              

49              /* Equivalence:

50               * bool Playing = true;

51               * bool Menu = false;

52               * bool Pause = false;

53               */

54               

55              Console.WriteLine("Gamestate: " + gamestate);

56              

57              //Interraction of the user:

58              int input=9;

59              while(input>0 && input<10)

60              {

61                  Console.WriteLine("Enter the option: ");

62                  input = Convert.ToInt16 (Console.ReadLine());

63                  switch(input)

64                  {

65                          case 1:

66                          {

67                              gamestate = Gamestate.Menu;

68                              Console.WriteLine("The active state: " + gamestate);

69                              break;

70                          }

71                          case 2:

72                          {

73                              gamestate = Gamestate.Pause;

74                              Console.WriteLine("The active state: " + gamestate);

75                              break;

76                          }

77                          case 3:

78                          {

79                              gamestate = Gamestate.Playing;

80                              Console.WriteLine("The active state: " + gamestate);

81                              break;

82                          }

83                          default:

84                          {

85                              Console.WriteLine("This is not a gamestate");

86                              break;

87                          }

88                  }

89              }

90              Console.WriteLine("Out of the loop!");

91              Console.ReadLine();

92              return 0;

93          }

94      }

95  }

96

programming_language.concurrent-programming:
*General-Programming-Knowledge*

0   

1   //Example of using multi-threading in C#

2   

3   using System;

4   using System.Threading;

5   

6   namespace MultithreadingApplication 

7   {

8      class ThreadCreationProgram 

9      {

10        public static void CallToChildThread() 

11        {

12           try 

13           {

14              Console.WriteLine("Child thread starts");

15              

16              // do some work, like counting to 10

17              for (int counter = 0; counter <= 10; counter++) 

18              {

19                 Thread.Sleep(500);

20                 Console.WriteLine(counter);

21              }

22              

23              Console.WriteLine("Child Thread Completed");

24           } 

25           catch (ThreadAbortException e) 

26           {

27              Console.WriteLine("Thread Abort Exception");

28           } 

29           finally 

30           {

31              Console.WriteLine("Couldn't catch the Thread Exception");

32           }

33        }

34        

35        static void Main(string[] args) 

36        {

37           ThreadStart childref = new ThreadStart(CallToChildThread);

38           Console.WriteLine("In Main: Creating the Child thread");

39           

40           Thread childThread = new Thread(childref);

41           childThread.Start();

42           

43           //stop the main thread for some time

44           Thread.Sleep(2000);

45           

46           //now abort the child

47           Console.WriteLine("In Main: Aborting the Child thread");

48           

49           childThread.Abort();

50           Console.ReadKey();

51        }

52     }

53  }

