programming_language.ternary-operator:
*General-Programming-Knowledge*

0   

1   //Example of using ternary operator in C#

2   

3   using System;

4   

5   namespace project11_ternary_operator

6   {

7       class Program

8       {

9           public static void Main(string[] args)

10          {

11              bool istrue;

12              int simpletest;

13              int number = 10;

14              

15              //Ternary Operator "?":

16              istrue = number==10 ? true : false;

17              //Syntax: boolean variable = condition ? value for true : value for false

18              

19              /* Equivalence of that:

20               * if(number==10)

21               * {

22               *         istrue=true;

23               * }

24               * else

25               * {

26               *         istrue=false;

27               * }

28               */

29              

30              Console.WriteLine("The value of istrue is: {0}", istrue);

31              

32              //Other example:

33              simpletest = number==10 ? 10+2 : 10-2;

34              Console.WriteLine("The value of simpletest is: {0}", simpletest);

35              

36              Console.Write("Press any key to continue . . . ");

37              Console.ReadKey(true);

38          }

39      }

40  }

