programming_language.datatype-conversions:
*General-Programming-Knowledge*

0   

1   //Example of datatype conversions:

2   

3   using System;

4   

5   namespace project13_implicit__explicit__data_type_conversions

6   {

7       class Program

8       {

9           public static void Main(string[] args)

10          {

11              /* An implicit conversion is done by the compiler when there are no loss of information. 

12               * Also there is no possibility of throwing exceptions during the conversion.

13               * 

14               * Example:

15               */

16               

17              int numb1 = 100;

18              float numb2 = numb1; //implicit conversion (done by the compiler).

19              Console.WriteLine("The value of numb2 is: {0}", numb2);

20              

21              /* An explicit conversion is done by the programmer using type cast operators  

22               * or predefined classes, data-conversion classes.

23               * 

24               * Example:

25               */

26              

27              float numb3=123.543f;

28              numb1 = (int) numb3; //explicit conversion using type cast.

29              Console.WriteLine("The value of numb1 is: {0}", numb1);

30              

31              numb1 = Convert.ToInt32(numb3); //explicit conversion using Conversion Class.

32              Console.WriteLine("The value of numb1 is: {0}", numb1);

33              

34              /* Conversion Class - will throw an exception/message when the conversion fails 

35               * because one variable can't hold the value of the converted one.

36               * Type Cast - will not throw any exception, will just print the minimum value that 

37               * the variable can hold. 

38               */

39              

40              //Parse Methods of converting string to int.

41              string valueN = "199";

42              numb1 = int.Parse(valueN); //parsing sinthax

43              

44              //in case if the string does not contain a valid number, an exception will be thrown. 

45              //So the program will be terminated.

46              Console.WriteLine("The value of numb1 after parsing is: {0}", numb1);

47              

48              //TryParse Methods of converting string to int.

49              string valueB = "221a";

50              int Result = 0;

51              bool Success = int.TryParse(valueB, out Result); //sinthax of TryParse.    

52              

53              //if int.TryParsing is succesfull the bool Success will become true, 

54              //else it will become false.

55              

56              //TryParse takes 2 arguments: the string to be converted 

57              //and the variable that will stored the converted result (if it is successful). 

58              

59              //If the TryParse is not successful the Result will not be changed.

60              

61              if(Success)

62              {

63                  Console.WriteLine("The value of numb1 after tryparsing is: {0}", Result );

64              }

65              else

66              {

67                  Console.WriteLine("The number is invalid. The conversion was unsuccesful!");

68              }

69              

70              Console.Write("Press any key to continue . . . ");

71              Console.ReadKey(true);

72          }

73      }

74  }

75  


0   

1   //Example of explicit vs implicit conversions

2   

3   using System;

4   

5   namespace project30_explicit_vs_implicit_implementation

6   {

7       interface I1

8       {

9           void Print(); 

10          

11          //when there are 2 interfaces with identical function, which inherits into the same child

12          //the explicit implementation must be used to avoid ambiguity.

13      }

14      interface I2

15      {

16          void Print();

17      }

18      interface I3

19      {

20          void MessageShow();

21      }

22      interface I4

23      {

24          void MessageShow();

25      }

26      

27      class Program: I1,I2,I3,I4 //this class inherits from all 4 interfaces

28      {

29          public static void Main(string[] args)

30          {

31              Program soft = new Program(); 

32              //soft is reference variable for an object type Program stored in heap.

33              

34              soft.Print(); //calling the implicit implemented function

35              

36              //By TYPE CASTING - calling the explicit implemented functions.

37              ((I3)soft).MessageShow(); //calling the explicit implemented function

38              ((I4)soft).MessageShow(); //calling the explicit implemented function

39              

40              //By OBJECT REFERENCE - calling the explicit implemented functions.

41              I3 explicit1 = new Program();

42              I4 explicit2 = new Program();

43              explicit1.MessageShow();

44              explicit2.MessageShow();

45              

46              //END OF PROGRAM;

47              Console.Write("Press any key to continue . . . ");

48              Console.ReadKey(true);

49          }

50          

51          //This is implicit implementation, the compilation and the runtime is fine, 

52          //dispite that there is a simple implementation for the both functions.

53          

54          public void Print()

55          {

56              Console.WriteLine("Implicit Implemetation!");

57          }

58          

59          //Explicit Implementation:

60          

61          //when explicitly implementing a function the access modifiers are not allowed

62          void I3.MessageShow() 

63          {

64              Console.WriteLine("This is I3 function!");

65          }

66          

67          //when explicitly implementing a function the access modifiers are not allowed

68          void I4.MessageShow() 

69          {

70              Console.WriteLine("This is I4 function!");

71          }

72      }

73  }

