programming_language.operators:
*General-Programming-Knowledge*

0   

1   //Example of using operators in C#

2   

3   using System;

4   

5   namespace project12_new_types_null_coalescing_operator

6   {

7       class Program

8       {

9           public static void Main(string[] args)

10          {

11              /* Non-Nullable type: Are Value TYPES: 

12               * integers, boolean, double, float, long, long long, char...etc

13               * 

14               * Nullable type: Are Reference TYPES: 

15               * classes, interface, delegates, arrays... etc

16               * 

17               * A Non-Nullable type can become Nullable type by using ? in their declaration.

18               * 

19               * Examples:

20               */

21              

22              bool major = false; //non-nullable value type.

23              bool ?minor = null; //nullable value type. 

24              

25              /* A nullable boolean type can hold on to 3 values: true, false or null. */

26              Console.WriteLine("Are you a minor? Press Y/N to answear or anything else to skip.");

27              char answear = Convert.ToChar(Console.ReadLine());

28              switch(answear)

29              {

30                  case 'y':  minor = true; break;

31                  case 'Y': minor = true; break;

32                  case 'n':  minor = false; break;

33                  case 'N': minor = false; break;

34                  default: minor = null; break;

35              }

36              

37              if(minor == true)

38              {

39                  Console.WriteLine("You are minor!");

40              }

41              else if(minor == false)

42              {

43                  Console.WriteLine("You are not minor!");

44              }

45              else

46              {    

47                  Console.WriteLine("You did not answear the question!");

48              }

49              

50              //NULL COALESCING OPERATOR:

51              int? TicketsOnSale = 100;

52              

53              //if TicketsOnSale is null, availableTickets will be 0, otherwise

54              //availableTickets = (int)TicketsOnSale.

55              int availableTickets=TicketsOnSale??0; 

56  

57              /*Equivalent method:

58               * if(TicketsOnSale == null)

59               * {

60               *         availableTickets=0;

61               * }

62               * else

63               * {

64               *         availableTickets = (int)TicketsOnSale; 

65               *         //TicketsOnSale is a nullable int, so it must be

66               *         //casted to normal int.

67               *         

68               *         //Other method of casting:

69               *         //available Tickets = TicketsOnSale.value;

70               * }

71               */

72              

73              Console.WriteLine("Available Tickets: {0}", availableTickets);

74              Console.Write("Press any key to continue . . . ");

75              Console.ReadKey(true);

76          }

77      }

78  }

