programming_language.recursion:
*General-Programming-Knowledge*

0   

1   //Example of using recursion in JavaScript

2   

3   function RecursiveFunc(num)

4   {

5       if (num <= 1)

6       {

7           return 1;

8       }

9       else

10      {

11          return num * RecursiveFunc(num-1);

12          //This will be equivalent to n* n-1* n-2* n-3* ...... *1; when it reaches 

13          //1 all the functions will end

14          

15      }

16      

17      /*

18          Using the loop the whole code above can be implemented as :

19          var result=1;

20          while(num > 1)

21          {

22              result*=num;

23              num-=1;

24          }

25      */

26  };

27  

28  document.write("This was a recursive function: " + RecursiveFunc(5) + "<br>");

29  

30  //Comparison to loops:

31  

32  document.write("<br>With Loops<br>");

33  var i;

34  for(i=0;i<10;i++)

35  {

36      document.write(i+" ");

37  }

38  

39  //The equivalent recursive function of the for loop above is:

40  

41  document.write("<br>With Recursion<br>");

42  

43  function Rec(a)

44  {

45      if(a<10)

46      {

47          document.write(a + " ");

48          Rec(a+1);

49      }

50  };

51  

52  Rec(0);

53

