1   //Example of reading content from file in JAVA:

2   

3   // Import the File class

4   import java.io.File;

5   

6   // Import this class to handle errors

7   import java.io.FileNotFoundException;

8   

9   // Import the Scanner class to read text files  

10  import java.util.Scanner; 

11  

12  public class ReadFile 

13  {

14      public static void main(String[] args) 

15      {

16          try 

17          {

18              File myObj = new File("filename.txt");

19              Scanner myReader = new Scanner(myObj);

20              while (myReader.hasNextLine()) 

21              {

22                  String data = myReader.nextLine();

23                  System.out.println(data);

24              }

25              myReader.close();

26          } 

27          catch (FileNotFoundException e) 

28          {

29              System.out.println("An error occurred.");

30              e.printStackTrace();

31          }

32      }

33  }

