programming_language.serialization:
*General-Programming-Knowledge*

0   

1   //Example of serialization in Java:

2   

3   package project23;

4   

5   import java.io.EOFException;

6   import java.io.File;

7   import java.io.FileInputStream;

8   import java.io.FileNotFoundException;

9   import java.io.FileOutputStream;

10  import java.io.IOException;

11  import java.io.ObjectInputStream;

12  import java.io.ObjectOutputStream;

13  import java.io.PrintWriter;

14  import java.util.ArrayList;

15  import java.util.Scanner;

16  

17  public class Project23 {

18  

19      /**

20       * @param args the command line arguments

21       */

22      

23      //Making main function able to throw IOException, 

24      //so that we don't need to use try catch blocks.

25      public static void main(String[] args) throws IOException 

26      {

27          //Creating a collection of HumanClass objects:

28          ArrayList<HumanClass> myList = new ArrayList();

29          myList.add(new HumanClass(22, "Adam Lambert"));

30          myList.add(new HumanClass(44, "Jackie Chan"));

31          

32          //Creating a file to store these objects:

33          File myObjectsFile = new File ("objectFile.txt");

34          

35          //Serializing an object:

36          try

37          {

38              FileOutputStream fo = new FileOutputStream(myObjectsFile);

39              ObjectOutputStream os = new ObjectOutputStream(fo);

40              for(HumanClass s:myList)

41              {

42                  //Writing objects to a file can be made only using binary mode. 

43                  //So that file will not contain readable text

44                  //and cannot be altered using text editors.

45                  os.writeObject(s);

46              }

47          }

48          catch(FileNotFoundException e)

49          {

50              System.out.printf("File not found: %s", e);

51          }

52          

53          //Keep the console opened until a key is pressed.

54          Scanner myscanner = new Scanner(System.in);

55          myscanner.next();

56      }

57  }

