programming_language.deserialization:
*General-Programming-Knowledge*



0   

1   //Example of deserialization in Java:

2   

3   package project23;

4   

5   import java.io.EOFException;

6   import java.io.File;

7   import java.io.FileInputStream;

8   import java.io.FileNotFoundException;

9   import java.io.FileOutputStream;

10  import java.io.IOException;

11  import java.io.ObjectInputStream;

12  import java.io.ObjectOutputStream;

13  import java.io.PrintWriter;

14  import java.util.ArrayList;

15  import java.util.Scanner;

16  

17  public class Project23 

18  {

19  

20      /**

21       * @param args the command line arguments

22       */

23      

24      //Making main function able to throw IOException, 

25      //so that we don't need to use try catch blocks.

26      public static void main(String[] args) throws IOException 

27      {

28          //Deserializing an object from the file:

29          ArrayList<HumanClass> ObjectRead = new ArrayList();

30          FileInputStream fi = new FileInputStream(myObjectsFile);

31          ObjectInputStream oi = new ObjectInputStream(fi);

32          try

33          {

34              //the following loop will terminate when the catch block will terminate, 

35              //and when the catch block will terminate then EOFException will be thrown.

36              while(true) 

37              {

38                  //this will read until the file ends.

39                  HumanClass e = (HumanClass) oi.readObject();  

40                  

41                  //When it cannot read any more objects from the file 

42                  //it will throw an EOFException that needs to be catched, and handled,

43                  //otherwise the program will terminate.

44                  ObjectRead.add(e);

45              }

46          }

47          catch(ClassNotFoundException e)

48          {

49               System.out.printf("File not found: %s", e);

50          }

51          //Once the file was done reading, it will throw EOFException

52          catch(EOFException b) 

53          {

54              System.out.println("\nDone reading the objects:\n");

55              

56              //Print the objects:

57              for(HumanClass s:ObjectRead)

58              {

59                  System.out.printf("Name: %s, Age: %d\n", s.GetName(), s.GetAge());

60              }

61          }

62          

63          //Keep the console opened until a key is pressed.

64          Scanner myscanner = new Scanner(System.in);

65          myscanner.next();

66      }

67  }

