programming_language.exception-handling:
*General-Programming-Knowledge*

0   

1   //Example of exception handling in Java:

2   //Example of throwing custom exceptions;

3   

4   package project22;

5   import java.util.InputMismatchException;

6   import java.util.Scanner;

7   

8   public class Project22 

9   {

10      public static void main(String[] args) 

11      {

12          Scanner myscanner = new Scanner(System.in);

13          try

14          {

15              //try block should contain the code that 

16              //might throw an exception:

17              System.out.println("Enter an integer: ");

18              int number = myscanner.nextInt();

19              

20              //This code will run only if the line above doesn't 

21              //produce an exception.

22              System.out.printf("You entered: %d\n\n", number);

23              

24              //Throwing a Custom Exception:

25              if(number>10||number<0)

26              {

27                  throw new CustomExceptionOutRange();

28              }

29          }

30          //Handle the input mismatch exception:

31          catch(InputMismatchException e)

32          {

33              System.out.println("You didn't entered an integer!\n");

34          }

35          //Handle the custom exception:

36          catch(CustomExceptionOutRange r)

37          {

38              System.out.println("Your number is out of range!\n");

39          }

40          //This code gets executed anyhow.

41          finally

42          {

43              //the finally block is not mandatory

44              //It is possible to have only try and catch blocks

45              

46              System.out.println("Finally block reached.");

47          }

48          myscanner.next();

49      }

50  }

