programming_language.random-generators:
*General-Programming-Knowledge*

0   

1   //Example of generating random numbers in Java:

2   

3   package project37;

4   

5   import java.util.Random;

6   import java.util.Scanner;

7   

8   public class Project37 {

9   

10      /**

11       * @param args the command line arguments

12       */

13       

14      public static void main(String[] args) 

15      {

16          Scanner myscanner = new Scanner(System.in);

17          System.out.printf("Thinking of a number between 0 and 100:\n\n");

18          

19          //Create a random generator

20          Random rand = new Random();

21          

22          //Generate a random number between 0-100

23          int Number = rand.nextInt(101);

24          

25          //This variable will store user input;

26          int choice = 101;

27          

28          //this will count how many times 

29          //the user guessed wrong

30          int NrOfTries=0;

31          

32          //Enter the loop by default:

33          while(Number!=choice)

34          {

35              System.out.printf("What is the number?\n");

36              do

37              {

38                  System.out.printf("Number = ");

39                  choice = myscanner.nextInt();

40                  //get input from the user;

41                  

42              }while(choice<0 || choice>100);

43              

44              //Compare the user input with the random number;

45              if(Number==choice)

46              {

47                  System.out.printf("Congratulations, the number was: %d\n", Number);

48              }

49              

50              else if(Number<choice)

51              {

52                 System.out.printf("Your number is too high, try a lower one!\n");

53              }

54              

55              else

56              {

57                  System.out.printf("Your number is too low, try a higher one!\n");

58              }

59              

60              System.out.println();

61              NrOfTries++;

62          }

63          

64          System.out.printf("You required %d tries to guess the number!\n", NrOfTries);

65          

66          //Keep the console opened.

67          myscanner.next();

68      }

69  }

