programming_language.command-line-arguments:
*General-Programming-Knowledge*

0   

1   //Working with command-line arguments in Java:

2   

3   package Args_main;

4   

5   import java.util.Scanner;

6   

7   public class Args_main

8   {

9       //args will store the command prompt arguments - if any

10      public static void main(String[] args) 

11      { 

12          //Supposingly we have some arguments in args[]

13          

14          int [] lenNrs = Args_main.NumberOrNo(args); 

15          //convert them to numbers or get their string length

16          

17          Sorting(lenNrs, args); 

18          //sort the numbers or lengths

19          

20          System.out.printf("Sorting by length or value: \n\n");

21          for (int i=0; i<args.length; i++)

22          {

23              //print results

24              System.out.printf("%d <--> %s\n", lenNrs[i], args[i]); 

25          }

26          

27          Scanner myscanner = new Scanner(System.in);

28          myscanner.next();

29          //cursor to the next line.

30      }

31      

32      //We shall return an array

33      public static int[] NumberOrNo(String[] arguments) 

34      {

35          //Build an array of the same length as arguments array

36          int[] someArray = new int[arguments.length]; 

37          for (int i=0; i<arguments.length; i++)

38          {

39              try

40              {

41                  //try to convert to int

42                  someArray[i] = Integer.parseInt(arguments[i]); 

43              }

44              catch (Exception e)

45              {

46                  //if not successful then we are having a letter string / word

47                  someArray[i] = arguments[i].length(); 

48                  //take out it's length

49              }

50          }

51          return someArray;

52      }

53      

54      //no need to return anything, lists/arrays are always passed by reference

55      public static void Sorting (int[] nrsOrNo, String[] arguments) 

56      {

57          //Do the sorting

58          for (int i=0; i < nrsOrNo.length; i++)

59          {

60              for (int j=0; j<nrsOrNo.length; j++ )

61              {

62                  if (nrsOrNo[j] < nrsOrNo[i])

63                  {

64                      int temp = nrsOrNo[i];

65                      nrsOrNo[i] = nrsOrNo[j];

66                      nrsOrNo[j] = temp;

67                      String argTmp = arguments[i];

68                      arguments[i] = arguments [j];

69                      arguments [j] = argTmp;

70                  }

71              }

72          }

73      }

74  }

75  

