1   //Example of using ternary operator in Java:

2   

3   import java.io.*;

4   

5   class Ternary 

6   {

7       public static void main(String[] args)

8       {

9           // variable declaration

10          int n1 = 5, n2 = 10, max;

11  

12          System.out.println("First num: " + n1);

13          System.out.println("Second num: " + n2);

14  

15          // Largest among n1 and n2

16          max = (n1 > n2) ? n1 : n2;

17  

18          // Print the largest number

19          System.out.println("Maximum is = " + max);

20      }

