programming_language.reflection:
*General-Programming-Knowledge*

0   

1   //Example of reflection in Java:

2   

3   import java.lang.Class;

4   import java.lang.reflect.*;

5   

6   class Animal {

7   }

8   

9   //put this class in different Dog.java file

10  public class Dog extends Animal 

11  {

12      public void display() 

13      {

14          System.out.println("I am a dog.");

15      }

16  }

17  

18  //put this in Main.java file

19  class Main 

20  {

21      public static void main(String[] args) 

22      {

23          try 

24          {

25              // create an object of Dog

26              Dog d1 = new Dog();

27  

28              // create an object of Class

29              // using getClass()

30              Class obj = d1.getClass();

31  

32              //get name of the class

33              String name = obj.getName();

34              System.out.println("Name: " + name);

35  

36              //get the access modifier of the class

37              int modifier = obj.getModifiers();

38  

39              //convert the access modifier to string

40              String mod = Modifier.toString(modifier);

41              System.out.println("Modifier: " + mod);

42  

43              //get the superclass of Dog

44              Class superClass = obj.getSuperclass();

45              System.out.println("Superclass: " + superClass.getName());

46          }

47  

48          catch (Exception e) 

49          {

50              e.printStackTrace();

51          }

52      }

53  }

