manifest.serialization:
            *General-Programming-Knowledge*


            Storing the state of an object to a file:


/*Example of C++ object.field serialization and deserialization*/

#include <iostream> 
#include <fstream> 

using namespace std;

class Student 
{
    public:

    string name;
    int rollno;
    string branch;

    Student () // default constructor
    {
    } 

    Student (string n, int r, string b) // overloading constructor
    {
        name = n;
        rollno = r;
        branch = b;
    }

    //override the insertion operator for object.field serialization
    friend ofstream & operator << (ofstream & ofs, Student s);
    //now we can write individually every class field member to the file.
    //Example: ofs << s1.name; 

    //override the extraction for object.field deserialization
    friend ifstream & operator >> (ifstream & ifs, Student & s);
    //now we can read individually every class field member from the file. 
    //Example: ifs >> s1.name; 
};

int main() 
{
    Student s1 ("James", 28, "IT");

    //Open the output stream
    ofstream ofs ("Student.txt", ios::trunc);

    //writing to the file
    ofs << s1.name << endl;
    ofs << s1.rollno << endl;
    ofs << s1.branch << endl;
    ofs.close();

    Student s2; //empty object

    //Open the input stream
    ifstream ifs("Student.txt");

    //reading from the file
    ifs >> s2.name;
    ifs >> s2.rollno;
    ifs >> s2.branch;
    ifs.close();

    cout << s2.name << endl;
    cout << s2.rollno << endl;
    cout << s2.branch << endl;

    /*
        OUTPUT: 
        James 
        28 
        IT
    */
}

manifest.deserialization:
            *General-Programming-Knowledge*


            Deserialization is the reverse process of Serialization, 
            where the byte stream is used to reconstruct the original object.


/*Example of C++ whole-object serialization and deserialization*/

#include <iostream> 
#include <fstream> 

using namespace std;
class Student
{
    public:

    string name;
    int rollno;
    string branch;
    Student () // default constructor
    {
    }

    Student (string n, int r, string b) // overloading constructor
    {
        name = n;
        rollno = r;
        branch = b;
    }

    //override the insertion operator for object.field serialization
    friend ofstream & operator << (ofstream & ofs, Student s);
    //now we can write individually every class field member to the file. 
    //Example: ofs << s1.name; 

    //override the extraction for object.field deserialization
    friend ifstream & operator >> (ifstream & ifs, Student & s);
    //now we can read individually every class field member from the file. 
    //Example: ifs >> s1.name; 
};

//Overriding the operators for whole-object serialization and deserialization
ofstream &  operator << (ofstream & ofs, Student s)
{
    ofs << s.name << endl;
    ofs << s.rollno << endl;
    ofs << s.branch << endl;
    return ofs;
}
ifstream & operator >> (ifstream & ifs, Student & s)
{
    ifs >> s.name >> s.rollno >> s.branch;
    return ifs;
}

/*
now we can read/write the whole object to and from the file. 

Example: 
ofs << s1; 
ifs >> s2; 

*/

int main()
{
    Student s1("James", 28, "IT");

    //Open the output stream
    ofstream ofs("Student.txt", ios::trunc);
    ofs <<  s1;
    ofs.close();

    Student s2; // empty object

    //Open the input stream
    ifstream ifs("Student.txt");
    ifs >> s2;
    ifs.close();

    cout << s2.name << " " << s2.rollno << " " << s2.branch << endl;

    /*
        OUTPUT:
        James 28 IT
    */
}

