manifest.recursion:
            *General-Programming-Knowledge*

//Example of using recursion in JavaScript

function RecursiveFunc(num)
{
    if (num <= 1)
    {
        return 1;
    }
    else
    {
        return num * RecursiveFunc(num-1);
        //This will be equivalent to n* n-1* n-2* n-3* ...... *1; when it reaches 
        //1 all the functions will end

    }

    /*
        Using the loop the whole code above can be implemented as :
        var result=1;
        while(num > 1)
        {
            result*=num;
            num-=1;
        }
    */
};

document.write("This was a recursive function: " + RecursiveFunc(5) + "<br>");

//Comparison to loops:

document.write("<br>With Loops<br>");
var i;
for(i=0;i<10;i++)
{
    document.write(i+" ");
}

//The equivalent recursive function of the for loop above is:

document.write("<br>With Recursion<br>");

function Rec(a)
{
    if(a<10)
    {
        document.write(a + " ");
        Rec(a+1);
    }
};

Rec(0);

