      //initialized with an object of type ArrayList() or LinkedList;

      a1.add("Zara");
      a1.add("Mahnaz");
      a1.add("Ayan");      
      System.out.println("ArrayList Elements:");
      System.out.print("\t" + a1);
      System.out.println();

      //Creating and printing a linked list.
      List l1 = new LinkedList();

      /*
